definition.script:
pipeline {
   // Execute the Pipeline, or stage, on any available agent  
   agent { 
           node { 
              label 'sofia2master' 
            } 
          }

   tools { 
      maven 'maven'
      jdk 'java8'
   }   

   environment {          
      // Base sources path 
      BASEPATH = '/datadrive/jenkins/.jenkins/jobs/{{MICROSERVICE_NAME}}/workspace'

      BASEDIR_PATH = '/datadrive/jenkins/.jenkins/jobs/{{MICROSERVICE_NAME}}/workspace/{{SOURCES_PATH}}/base'
      BASEPYTHONDIR_PATH = '/datadrive/jenkins/.jenkins/jobs/{{MICROSERVICE_NAME}}/workspace/{{SOURCES_PATH}}/base-python'
      NOTEBOOKDIR_PATH = '/datadrive/jenkins/.jenkins/jobs/{{MICROSERVICE_NAME}}/workspace/{{SOURCES_PATH}}/notebook'

      SOURCESPATH = '/datadrive/jenkins/.jenkins/jobs/{{MICROSERVICE_NAME}}/workspace/{{SOURCES_PATH}}'
      //DOCKERPATH = '/datadrive/jenkins/.jenkins/jobs/{{MICROSERVICE_NAME}}/workspace/{{DOCKER_PATH}}'
      MICROSERVICE_NAME = '{{MICROSERVICE_NAME}}'
      //CATTLE_ACCESS_KEY = '1CD18DB0F67C50608DC3'
      //CATTLE_SECRET_KEY = 'VXAowSGp27LnNXATGFqqVPwxx9EiPDBSgkjPx8nv'
   }

   parameters { 
       string(name: 'GIT_URL', 
              defaultValue: '{{GIT_REPOSITORY}}', 
              description: 'URL Git Repository')
       string(name: 'GIT_USER', 
              defaultValue: '{{GIT_USER}}', 
              description: 'URL Git user')
       string(name: 'GIT_TOKEN', 
              defaultValue: '{{GIT_TOKEN}}', 
              description: 'Git private tokeny')
       string(name: 'GIT_BRANCHNAME', 
              defaultValue: 'master', 
              description: 'deploy branch name')
       string(name: 'DOCKER_MODULETAGVALUE', 
              defaultValue: 'latest', 
              description: 'Image generation tag value')
       string(name: 'DOCKER_USERNAMEVALUE', 
              defaultValue: 'onesaitplatform', 
              description: 'Image registry image namespace')
       string(name: 'DOCKER_PUSHREGISTRY_PRIVATE', 
              defaultValue: 'true', 
              description: 'Push image to private registry')
       string(name: 'PRIVATE_REGISTRY', 
              defaultValue: 'registry.onesaitplatform.com', 
              description: 'URL to the docker registry')  
   }    

   stages {
       stage('Fetching from repository') {  
        steps {  
           sh "git clone \$(echo ${params.GIT_URL} | sed -e 's/:\\/\\//:\\/\\/${params.GIT_USER}:${params.GIT_TOKEN}@/') ${env.BASEPATH}"           
        }          
       }

     stage('Build Artifacts') {
        steps {
         // Run maven build
         dir("${env.SOURCESPATH}") {
         	sh "git checkout ${params.GIT_BRANCHNAME}"
           }
        }
     }

     stage('Generate base image') {  
        steps { 
            dir("${env.BASEDIR_PATH}") { 
                echo "Generating base image"
                sh "docker build -t zeppelin_alpine ."
            }
        }          
     }

     stage('Generate base-python image') {  
        steps { 
            dir("${env.BASEPYTHONDIR_PATH}") { 
                echo "Generating base-python image"
                sh "docker build -t zeppelin_alpine_python ."
            }
        }          
     }


     stage('Generate Docker notebook image') {
   		steps {
           dir("${env.NOTEBOOKDIR_PATH}") {
                echo "Generating notebook image"
                sh "docker build -t ${params.DOCKER_USERNAMEVALUE}/{{MICROSERVICE_NAME}}:${DOCKER_MODULETAGVALUE} ."
                sh "docker image inspect ${params.DOCKER_USERNAMEVALUE}/{{MICROSERVICE_NAME}}:${DOCKER_MODULETAGVALUE}"
           }
        }
     }

     stage('Push Docker images') {
        when {
          expression { params.DOCKER_PUSHREGISTRY_PRIVATE == 'true' }
        }
        steps {
          sh "docker tag ${params.DOCKER_USERNAMEVALUE}/{{MICROSERVICE_NAME}}:${params.DOCKER_MODULETAGVALUE} $PRIVATE_REGISTRY/${params.DOCKER_USERNAMEVALUE}/{{MICROSERVICE_NAME}}:${params.DOCKER_MODULETAGVALUE}"
          sh "docker push $PRIVATE_REGISTRY/${params.DOCKER_USERNAMEVALUE}/{{MICROSERVICE_NAME}}:${params.DOCKER_MODULETAGVALUE}"

        }
     }

   }

    post {
        always {
        	echo "Delete pushed image"
        	sh "docker rmi -f \$(docker images | grep \$DOCKER_USERNAMEVALUE/ | awk '{print \$3}' | uniq) || true"

        	echo 'Clean up workspace...'
        	sh "rm -r ${env.BASEPATH}"
        }   
	    failure {   
	    	echo "Delete Untagged images"
        	sh "docker rmi -f \$(docker images -f dangling=true -q) || true"
	    }
    }

}
