      for (var i = 0; i < vm.filters.length; i++) {
        if (vm.filters[i].id == 'entity') {
          vm.filters[i].data.optionsSelected = this.valueEntity;
        }
        else if (vm.filters[i].id == 'user') {
          vm.filters[i].data.optionsSelected = this.valueUsers;
        }
        else if (vm.filters[i].id == 'source') {
          vm.filters[i].data.optionsSelected = this.valueSource;
        }
        else if (vm.filters[i].id == 'status') {
          vm.filters[i].data.optionsSelected = this.valueStatus;
        } else if (vm.filters[i].id == 'queryType') {
          vm.filters[i].data.optionsSelected = this.valueQueryType;
        }
      }
      vm.sendFilters();
    },
    reset: function (e) {

      this.valueEntity = [];
      this.valueQueryType = [];
      this.valueSource = [];
