      if (column.order === null || column.order === 'ascending') {
        this.sortOrder = true;
      } else {
        this.sortOrder = false
      }
      this.loadItems()
    },
    handleCurrentChange(val) {
      this.page = val
      this.loadItems();
    }// load items is what brings back the rows from server
    , loadItems() {
      var that = this;
      //that.totalRecords = 2000;




      var filters = [];
      if (this.valueEntity.length > 0) {
        filters.push({ field: 'entity', op: "IN", exp: "('" + this.valueEntity.join("', '") + "')" });
      }
      if (this.valueUsers.length > 0) {
        filters.push({ field: 'user', op: "IN", exp: "('" + this.valueUsers.join("', '") + "')" });
      }
      if (this.valueSource.length > 0) {
        filters.push({ field: 'source', op: "IN", exp: "('" + this.valueSource.join("', '") + "')" });
      }
      if (this.valueStatus.length > 0) {
        filters.push({ field: 'status', op: "IN", exp: "('" + this.valueStatus.join("', '") + "')" });
      }
      if (this.valueQueryType.length > 0) {
        filters.push({ field: 'queryType', op: "IN", exp: "('" + this.valueQueryType.join("', '") + "')" });
      }
      if (this.valueDate.length > 0) {
        var date = Date.now();
       if (this.valueDate == 'last hour') {
        date = date - 3600000;
      }
      else if (this.valueDate == 'last 12 hours') {
        date = date - 43200000
      }
      else if (this.valueDate == 'last 24 hours') {
        date = date - 86400000
      }
      else if (this.valueDate == 'last 7 days') {
        date = date - 604800000

      }
      else if (this.valueDate == 'last 1 month') {
        date = date - 2592000000
      }
      else if (this.valueDate == 'last 3 months') {
        date = date - 77760000000
      }
      filters.push({ field: 'startTime', op: ">=", exp: date });

    }
      filters.push({ field: 'totalMs', op: ">=", exp: this.timeValue[0] });
    filters.push({ field: 'totalMs', op: "<=", exp: this.timeValue[1] });

    vm.from('QueryMetrics_count_all').filter(filters).exec().then(data => {
      if (data.length == 0) {
        that.totalRecords = 0;
        that.tableData = [];
      } else {
        that.totalRecords = data[0].total;
        var pag = that.currentPage - 1;
        if (that.sortField != null) {
          vm.from("QueryMetrics_all_disorderly").filter(filters).sort([{ "field": that.sortField, "asc": that.sortOrder }]).offset(that.pageSize * pag).limit(that.pageSize).exec().then(data => {
            that.tableData = data.map(x => {
              x.startTime = moment.utc(x.startTime).format('DD.MM.YYYY HH:mm:ss');
              x.endTime = moment.utc(x.endTime).format('DD.MM.YYYY HH:mm:ss');
              return x;
            });;
          })
        } else {
          vm.from("QueryMetrics_all_disorderly").filter(filters).offset(that.pageSize * pag).limit(that.pageSize).exec().then(data => {
            that.tableData = data.map(x => {
              x.startTime = moment.utc(x.startTime).format('DD.MM.YYYY HH:mm:ss');
              x.endTime = moment.utc(x.endTime).format('DD.MM.YYYY HH:mm:ss');
              return x;
            });;
          })
        }
      }



    })







  },
  search: function (e) {
    this.loadItems();
  },
  reset: function (e) {

    this.valueEntity = [];
    this.valueQueryType = [];
    this.valueSource = [];
    this.valueStatus = [];
    this.valueUsers = [];
    this.valueDate='';
    this.timeValue = [0, this.maxTimeValue + 100];
    this.search();
  },


