definition.script:
pipeline {
   // Execute the Pipeline, or stage, on any available agent
   agent {
           node {
              label 'sofia2master'
            }
          }

   tools {
      maven 'maven'
      jdk 'java8'
   }

   environment {
      MICROSERVICE_NAME = '{{MICROSERVICE_NAME}}'
   }

   parameters {
       string(name: 'MLFLOW_TRACKING_URI',
              defaultValue: '{{MLFLOW_TRACKING_URI}}controlpanel/modelsmanager',
              description: 'URI to the MLFlow instance')
       string(name: 'DOCKER_MODULETAGVALUE',
              defaultValue: 'latest',
              description: 'Image generation tag value')
       string(name: 'DOCKER_USERNAMEVALUE',
              defaultValue: 'onesaitplatform',
              description: 'Image registry image namespace')
       string(name: 'DOCKER_PUSHREGISTRY_PRIVATE',
              defaultValue: 'true',
              description: 'Push image to private registry')
       string(name: 'PRIVATE_REGISTRY',
              defaultValue: 'registry.onesaitplatform.com',
              description: 'URL to the docker registry')
   }

   stages {
     stage('Generate Docker images') {
        steps {
            sh "mlflow models build-docker -m \"runs:/{{MODEL_RUN_ID}}/model\" -n \"${params.DOCKER_USERNAMEVALUE}/${env.MICROSERVICE_NAME}:${params.DOCKER_MODULETAGVALUE}\""
        }
     }

     stage('Push Docker images') {
        when {
          expression { params.DOCKER_PUSHREGISTRY_PRIVATE == 'true' }
        }
        steps {
          sh "docker tag ${params.DOCKER_USERNAMEVALUE}/${env.MICROSERVICE_NAME}:${params.DOCKER_MODULETAGVALUE} $PRIVATE_REGISTRY/${params.DOCKER_USERNAMEVALUE}/${env.MICROSERVICE_NAME}:${params.DOCKER_MODULETAGVALUE}"
          sh "docker push $PRIVATE_REGISTRY/${params.DOCKER_USERNAMEVALUE}/${env.MICROSERVICE_NAME}:${params.DOCKER_MODULETAGVALUE}"
        }
     }
   }

    post {
        always {
          echo "Delete Untagged images"
          sh "docker rmi -f \$(docker images -f dangling=true -q) || true"

          echo "Delete pushed image"
          sh "docker rmi -f \$(docker images | grep \$DOCKER_USERNAMEVALUE/ | awk '{print \$3}' | uniq) || true"

        }
    }

}
